mapper.update:
update user_friend


        remark = #{remark},


where user_id = #{userId} and friend_id = #{friendId}

mapper.insert:
INSERT INTO friend_group_members(group_id, member_id)
SELECT id, #{memberId}
FROM friend_groups
WHERE user_id = #{userId} and group_name = #{groupName}

mapper.delete:
DELETE FROM friend_group_members
WHERE group_id IN (
    SELECT id
    FROM friend_groups
    WHERE user_id = #{userId} AND group_name = #{groupName}
)
  AND member_id = #{memberId}

mapper.select:
SELECT group_name
FROM friend_groups fgs, friend_group_members fgm
WHERE fgs.id = fgm.group_id and fgm.member_id = #{fromUserId} and fgs.user_id = #{toUserId}
